mapper.select:
select id_ from sys_role where 1=1

	and dept_id = #{dept_id}


	and role_name like CONCAT('%',#{keyword},'%')

select CONCAT(menu_name,'(', GROUP_CONCAT(code_text), ')') from sys_role_menu rm join sys_menu m on rm.menu_id=m.id_ join sys_dic d on rm.permission_=d.code_ join sys_dic_index di on d.index_id=di.id_ and di.key_='CRUD' where 1=1
and rm.role_id=#{roleId}
group by menu_name order by menu_id
